package week1day1hw;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numOne=3;
		float numTwo=5.6f;
		double numThree= 250.65d;
		long numFour=192;
		char s='d';
		boolean bol=true;
		short shn=12;
		byte b=6;
		System.out.println("the integer is: "+numOne);
		System.out.println("The float value is: "+numTwo);
		System.out.println("The double value is: "+numThree);
		System.out.println("The long value is: "+numFour);
		System.out.println("The character is: "+s);
		System.out.println("The boolean value is: "+bol);
		System.out.println("The short value is: "+shn);
		System.out.println("The byte value is: "+b);
	}

}
